pragma solidity ^0.4.24;

import "./interfaces/IRegistry.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";


contract Stats {
  using SafeMath for uint256;

  IRegistry public registry;

  uint256 public totalPlayCount;
  uint256 public totalWinCount;
  uint256 public totalWinVolume;
  uint256 public totalReferralCount;
  uint256 public totalReferralVolume;

  modifier onlyRps() {
    require(msg.sender == registry.getEntry("RockPaperScissors"));

    _;
  }

  function isContract(
    address _address
  )
    internal
    view
    returns (bool)
  {
    uint256 _size;
    assembly { _size := extcodesize(_address) }
    return _size > 0;
  }

  constructor(
    address _registryAddress
  )
    public
  {
    require(isContract(_registryAddress));
    registry = IRegistry(_registryAddress);
  }

  function incrementPlayCount()
    external
    onlyRps
  {
    totalPlayCount++;
  }

  function incrementWinCount()
    external
    onlyRps
  {
    totalWinCount++;
  }

  function incrementWinVolume(
    uint256 _winAmount
  )
    external
    onlyRps
  {
    totalWinVolume = totalWinVolume.add(_winAmount);
  }

  function incrementReferralCount()
    external
    onlyRps
  {
    totalReferralCount++;
  }

  function incrementReferralVolume(
    uint256 _referralAmount
  )
    external
    onlyRps
  {
    totalReferralVolume = totalReferralVolume.add(_referralAmount);
  }
}
